def validate_requires_args(self, args):
    """Check if all the required arguments have been provided. """
    silent_args = self.get_silent_args(args)

    # Precompute conditionally required args for each parser
    condition_req_args_map = {}
    for command_data in self.spec_helper.iterate_parsers():
        cmd_name = command_data['name']
        condition_req_args_map[cmd_name] = self._get_conditionally_required_args(
            cmd_name, 
            self.spec_helper.get_parser_option_specs(cmd_name), 
            args
        )

    def validate_parser(parser_name, expected_options, parser_args):
        """Helper method to resolve dict_merge. """
        result = collections.defaultdict(list)
        # Retrieve precomputed conditionally required args
        condition_req_args = condition_req_args_map[parser_name]
        for option in expected_options:
            name = option['name']
            # Check if argument is required (either normally or conditionally)
            if option.get('required', False) or name in condition_req_args:
                # Check if argument is missing and not silent
                if name not in parser_args and name not in silent_args:
                    result[parser_name].append(name)
        return result

    res = {}
    for command_data in self.spec_helper.iterate_parsers():
        cmd_name = command_data['name']
        # Only process commands present in args
        if cmd_name in args:
            dict_utils.dict_merge(res, validate_parser(cmd_name, 
                self.spec_helper.get_parser_option_specs(cmd_name), 
                args[cmd_name]))
    missing_args = dict((cmd_name, args) for cmd_name, args in res.items() if
        len(args) != 0)
    return missing_args

def _get_conditionally_required_args(self, command_name, options_spec, args):
    required_args = []
    for option_spec in options_spec:
        if 'required_when' in option_spec:
            condition = option_spec['required_when']
            if eval(condition, {}, args):
                required_args.append(option_spec['name'])
    return required_args

