def validate_as_prior_version(self, prior):
    current_map = self.this_map
    prior_map = prior.get_logical_path_map()

    # Check that prior's paths are subset of current paths
    prior_paths = set(prior_map.keys())
    current_paths = set(current_map.keys())
    if not prior_paths.issubset(current_paths):
        missing = prior_paths - current_paths
        self.error(f"Prior inventory contains paths not present in current: {', '.join(missing)}")
        return False

    # Compare states between inventories
    errors = self.compare_states_for_version(prior, self)
    if errors:
        if isinstance(errors, dict):
            for path, message in errors.items():
                self.error(f"Path {path}: {message}")
        else:
            for message in errors:
                self.error(message)
        return False

    return True

def validate_as_prior_version(self, prior):
    # Check digest algorithm compatibility
    if hasattr(self, 'digest_algorithm') and hasattr(prior, 'digest_algorithm'):
        if self.digest_algorithm != prior.digest_algorithm:
            self.error(f"Prior inventory uses digest algorithm {prior.digest_algorithm} but current uses {self.digest_algorithm}")
            return False

    current_map = self.this_map
    prior_map = prior.get_logical_path_map()

    # Check that prior's paths are subset of current paths
    prior_paths = set(prior_map.keys())
    current_paths = set(current_map.keys())
    if not prior_paths.issubset(current_paths):
        missing = prior_paths - current_paths
        self.error(f"Prior inventory contains {len(missing)} paths not present in current: {', '.join(sorted(missing)[:5])}{'...' if len(missing) > 5 else ''}")
        return False

    # Compare states between inventories
    errors = self.compare_states_for_version(prior, self)
    if errors:
        if isinstance(errors, dict):
            for path, message in errors.items():
                self.error(f"Content mismatch at {path}: {message}")
        else:
            for i, message in enumerate(errors, 1):
                self.error(f"Validation error #{i}: {message}")
        return False

    return True

def validate_as_prior_version(self, prior):
    # Check version compatibility
    if hasattr(self, 'version') and hasattr(prior, 'version'):
        if self.version <= prior.version:
            self.error(f"[VersionCompatibility] Prior version {prior.version} must be strictly less than current version {self.version}")
            return False
    else:
        self.error("[VersionCompatibility] Both inventories must have version attributes")
        return False

    # Check digest algorithm compatibility
    if hasattr(self, 'digest_algorithm') and hasattr(prior, 'digest_algorithm'):
        if self.digest_algorithm != prior.digest_algorithm:
            self.error(f"[DigestAlgorithmMismatch] Prior inventory uses digest algorithm {prior.digest_algorithm} but current uses {self.digest_algorithm}")
            return False

    current_map = self.this_map
    prior_map = prior.get_logical_path_map()

    # Check that prior's paths are subset of current paths
    prior_paths = set(prior_map.keys())
    current_paths = set(current_map.keys())
    if not prior_paths.issubset(current_paths):
        missing = prior_paths - current_paths
        self.error(f"[MissingPaths] Prior inventory contains {len(missing)} paths not present in current: {', '.join(sorted(missing)[:5])}{'...' if len(missing) > 5 else ''}")
        return False

    # Compare metadata between inventories
    if hasattr(self, 'metadata') and hasattr(prior, 'metadata'):
        if self.metadata != prior.metadata:
            self.error("[MetadataMismatch] Prior inventory metadata does not match current metadata")
            return False

    # Compare states between inventories
    errors = self.compare_states_for_version(prior, self)
    if errors:
        if isinstance(errors, dict):
            for path, message in errors.items():
                self.error(f"[ContentMismatch] Content mismatch at {path}: {message}")
        else:
            for message in errors:
                self.error(f"[ValidationError] {message}")
        return False

    return True

def get_logical_path_map(inventory: dict, version: str) -> dict:
    """Get a map of logical paths in state to files on disk for version in inventory.

    Returns a dictionary: logical_path_in_state -> set(content_files)

    The set of content_files may includes references to duplicate files in
    later versions than the version being described.
    """
    if version not in inventory:
        return {}
    version_data = inventory[version]
    result = {}
    for state, state_data in version_data.items():
        for logical_path, content_files in state_data.items():
            if logical_path not in result:
                result[logical_path] = set()
            result[logical_path].update(content_files)
    return result

def get_logical_path_map(inventory: dict, version: str) -> dict:
    """Get a map of logical paths in state to files on disk for version in inventory.

    Returns a dictionary: logical_path_in_state -> set(content_files)

    The set of content_files may includes references to duplicate files in
