def match_pubdate(node, pubdate_xpaths):
    """
    Retorna o primeiro match da lista de pubdate_xpaths
    """
    if node is None:
        return None

    find_method = getattr(node, 'find', None)
    if find_method is None or not callable(find_method):
        return None

    if pubdate_xpaths is None:
        return None

    if isinstance(pubdate_xpaths, str):
        return None

    try:
        iterator = iter(pubdate_xpaths)
    except TypeError:
        return None

    matches = (find_method(xpath) for xpath in iterator)
    element = next((m for m in matches if m is not None), None)
    return element.text if element is not None else None

