def is_local(host):
    # Normalize the host to lowercase for case-insensitive comparison
    host_lower = host.lower()

    # Standard local identifiers
    local_identifiers = {'localhost', '127.0.0.1', '::1'}

    # Add the local hostname (dynamic)
    try:
        local_identifiers.add(socket.gethostname().lower())
    except:
        pass  # Proceed if hostname resolution fails

    # Resolve loopback addresses to get associated names and aliases
    loopback_ips = ['127.0.0.1', '::1']
    for ip in loopback_ips:
        try:
            # Get hostname and aliases for the loopback IP
            names = socket.gethostbyaddr(ip)
            # Add primary name and aliases in lowercase
            if names[0]:
                local_identifiers.add(names[0].lower())
            for alias in names[1]:
                local_identifiers.add(alias.lower())
        except (socket.herror, socket.gaierror):
            continue  # Skip if resolution fails

    return host_lower in local_identifiers

