def find_roots(
    graph: "Graph", prop: "URIRef", roots: Optional[Set["Node"]] = None
) -> Set["Node"]:
    non_roots = set()
    candidate = set() if roots is None else None

    for s, o in graph.subject_objects(prop):
        non_roots.add(s)
        if roots is None:
            candidate.add(s)
            candidate.add(o)

    if roots is None:
        roots = candidate - non_roots
    else:
        roots = roots - non_roots

    return roots

