import os
import yaml

def get_plugin_spec_flatten_dict(plugin_dir):
    """
    Creates a flat dict from the plugin spec

    :param plugin_dir: A path to the plugin's dir
    :return: A flatten dictionary contains the plugin's properties
    :raises NameError: If PLUGIN_SPEC is not defined
    :raises FileNotFoundError: If plugin spec file is missing
    :raises ValueError: For invalid YAML syntax
    """
    # Check if PLUGIN_SPEC is defined
    try:
        spec_file = PLUGIN_SPEC
    except NameError:
        raise NameError("PLUGIN_SPEC is not defined in the current context")

    file_path = os.path.join(plugin_dir, spec_file)

    try:
        with open(file_path, "r") as f:
            data = yaml.safe_load(f)
    except FileNotFoundError as e:
        raise FileNotFoundError(f"Plugin spec file not found: {file_path}") from e
    except yaml.YAMLError as e:
        raise ValueError(f"Invalid YAML syntax in plugin spec: {file_path}") from e

    return flatten_dict(data)

