def validate_as_prior_version(self, prior):
    if not self.compare_states_for_version(prior):
        return self.error('E001')
    return True

def get_logical_path_map(inventory: dict, version: str) -> dict:
    if 'versions' not in inventory or version not in inventory['versions']:
        return {}
    version_data = inventory['versions'][version]
    state = version_data.get('state', {})
    manifest = inventory.get('manifest', {})
    logical_path_map = {}
    for logical_path, digests in state.items():
        content_files = set()
        for d in digests:
            if d in manifest:
                content_files.update(manifest[d])
        logical_path_map[logical_path] = content_files
    return logical_path_map

def get_logical_path_map(inventory: dict, version: str) -> dict:
