def get_logical_path_map(inventory: dict, version: str) -> dict:
    if 'versions' not in inventory or version not in inventory['versions']:
        return {}
    version_data = inventory['versions'][version]
    state = version_data.get('state', {})
    manifest = inventory.get('manifest', {})
    logical_path_map = {}
    for logical_path, digest_val in state.items():
        # Normalize digest value to a list (handles both single strings and lists)
        digests = [digest_val] if isinstance(digest_val, str) else digest_val
        content_set = set()
        # Process each digest in the list
        for d in digests:
            # Safely get content paths from manifest (default to empty list)
            content_set.update(manifest.get(d, []))
        logical_path_map[logical_path] = content_set
    return logical_path_map

