def tzname_in_python2(namefunc):
    from functools import wraps
    @wraps(namefunc)
    def wrapper(self, dt):
        result = namefunc(self, dt)
        if PY2 and result is not None:
            return result.encode('utf-8')
        return result
    return wrapper

