    if not os.path.isfile(yaml_file):
        raise IRValidatorException("File not found: {}".format(yaml_file))

    try:
        with open(yaml_file, 'r', encoding='utf-8') as file:
            content = file.read()
    except OSError as exc:
        raise IRValidatorException("Error reading file: {}".format(yaml_file)) from exc

    try:
        cls.validate_from_content(content)
    except yaml.YAMLError as exc:
        raise IRValidatorException("Invalid YAML file: {}".format(yaml_file)) from exc

    data = yaml.safe_load(content)
    return data

def _include_groups(self, parser_dict):
    # Check if include_groups directive exists
    include_list = parser_dict.get("include_groups")
    if include_list is not None:
        # Get groups section or raise exception if missing
        groups = parser_dict.get("groups")
        if groups is None:
            raise SpecParserException(["No 'groups' section found for inclusion"])

        # Create mapping of group titles to groups
        group_map = {}
        for group in groups:
            title = group["title"]
            if title not in group_map:
                group_map[title] = group

        # Initialize new groups list
        new_groups = []
        # Process each group in include list
        for group_name in include_list:
            if group_name in group_map:
                # Add deep copy of matching group
                new_groups.append(deepcopy(group_map[group_name]))
            else:
                raise SpecParserException(["Group '{}' not found for inclusion".format(group_name)])

        # Replace groups with filtered list
        parser_dict["groups"] = new_groups

