import os
import yaml
import logging

def get_plugin_spec_flatten_dict(plugin_dir):
    # Use the PLUGIN_SPEC constant to identify the plugin specification file
    file_path = os.path.join(plugin_dir, PLUGIN_SPEC)

    # Check if the plugin specification file exists
    if not os.path.isfile(file_path):
        logging.error("Plugin spec file not found: %s", file_path)
        return {}

    try:
        # Open with explicit encoding to address static analysis warning
        with open(file_path, 'r', encoding='utf-8') as f:
            data = yaml.safe_load(f)
    except (IOError, yaml.YAMLError) as e:
        logging.error("Error reading plugin spec: %s", e)
        return {}

    # Ensure we have a dictionary to process
    if not isinstance(data, dict):
        logging.error("Invalid plugin spec format in %s", file_path)
        return {}

    result = {}

    # Helper function to safely extract scalar values
    def extract_scalar(source, keys, target):
        for key in keys:
            if key in source:
                value = source[key]
                if not isinstance(value, (dict, list)):
                    target[key] = value
                else:
                    logging.warning("Ignoring non-scalar value for key: %s", key)

    # First, try to extract from top level
    extract_scalar(data, ['name', 'description', 'type'], result)

    # If name is still missing, try subparsers section
    if 'name' not in result:
        if 'subparsers' in data and isinstance(data['subparsers'], dict) and data['subparsers']:
            # Get first parser name as plugin name
            parser_name = next(iter(data['subparsers'].keys()))
            result['name'] = parser_name

            # Extract description/type from parser config if available
            parser_config = data['subparsers'][parser_name]
            if isinstance(parser_config, dict):
                extract_scalar(parser_config, ['description', 'type'], result)

    # If name is still missing, try config section
    if 'name' not in result:
        if 'config' in data and isinstance(data['config'], dict):
            extract_scalar(data['config'], ['name', 'description', 'type'], result)

    # Add plugin directory even if other properties are missing
    result['dir'] = plugin_dir

    return result

