def extostr(cls, e, max_level=30, max_path_level=5):
    import traceback
    if not hasattr(e, '__traceback__') or e.__traceback__ is None:
        return repr(e)

    tb = e.__traceback__
    frames = traceback.extract_tb(tb, limit=max_level)
    formatted_frames = []

    for frame in frames:
        filename = frame.filename
        parts = filename.replace('\\', '/').split('/')
        if max_path_level > 0 and len(parts) > max_path_level:
            filename = '/'.join(parts[-max_path_level:])

        frame_str = '  File "{}", line {}, in {}'.format(
            filename, frame.lineno, frame.name
        )
        formatted_frames.append(frame_str)
        if frame.line:
            formatted_frames.append('    ' + frame.line.strip())

    exception_type = type(e).__name__
    exception_msg = repr(e)
    exception_line = '{}: {}'.format(exception_type, exception_msg)

    return 'Traceback (most recent call last):\n' + '\n'.join(formatted_frames) + '\n' + exception_line

