import socket

def is_local(host):
    """
    Checks if the host is the localhost

    :param host: The hostname or ip
    :return: True if the host is the localhost
    """
    if host is None:
        return False

    host_lower = host.lower()

    # Check common localhost names
    if host_lower in ['localhost', 'localhost.localdomain']:
        return True

    # Check loopback IPs using socket conversions
    try:
        # IPv4 loopback (127.0.0.0/8)
        ipv4_bytes = socket.inet_pton(socket.AF_INET, host_lower)
        if ipv4_bytes[0] == 127:
            return True
    except socket.error:
        pass

    try:
        # IPv6 loopback (::1)
        if socket.inet_pton(socket.AF_INET6, host_lower) == b'\x00'*15 + b'\x01':
            return True
    except socket.error:
        pass

    # Check against system hostnames and aliases
    try:
        local_hostname = socket.gethostname().lower()
        fqdn = socket.getfqdn().lower()

        # Get all system hostnames and aliases
        host_info = socket.gethostbyname_ex(local_hostname)
        all_names = {local_hostname, fqdn}
        all_names.update(alias.lower() for alias in host_info[1])

        if host_lower in all_names:
            return True
    except socket.error:
        pass

    return False

