def pop_u16(self) -> int:
    if len(self.data) < 2:
        raise IndexError("Insufficient data for 16-bit unsigned integer")
    b1 = self.data.pop()  # Least significant byte (last in buffer)
    b0 = self.data.pop()  # Most significant byte (second last in buffer)
    return (b0 << 8) | b1

