def get_logical_path_map(inventory: dict, version: str) -> dict:
    # Check if the inventory has a 'versions' key and if the requested version exists
    if 'versions' not in inventory:
        return {}
    versions = inventory['versions']
    if version not in versions:
        return {}
    version_data = versions[version]
    # Get the state dictionary for the version, default to empty dict if not present
    state_dict = version_data.get('state', {})
    # Build the result: for each logical path, map to a set of content files
    result = {}
    for logical_path, content_paths in state_dict.items():
        # Each content_path in the list is relative to the content directory
        # The full path relative to the object root is: f"{version}/content/{path}"
        full_paths = {f"{version}/content/{path}" for path in content_paths}
        result[logical_path] = full_paths
    return result

