def tzname_in_python2(namefunc):
    if PY2:
        from functools import wraps

        @wraps(namefunc)
        def wrapper(*args, **kwargs):
            s = namefunc(*args, **kwargs)
            if s is None:
                return None
            return s.encode('utf-8')
        return wrapper
    else:
        return namefunc

