def is_local(host):
    """
    Checks if the host is the localhost

    :param host: The hostname or ip
    :return: True if the host is the localhost
    """
    # Create a set of local identifiers (case-insensitive)
    local_identifiers = {'localhost', '127.0.0.1', '::1'}

    # Add current hostname in lowercase
    try:
        local_identifiers.add(socket.gethostname().lower())
    except:
        pass  # Proceed if hostname can't be retrieved

    # Resolve loopback addresses to get canonical names and aliases
    for addr in ['127.0.0.1', '::1']:
        try:
            canonical, aliases, _ = socket.gethostbyaddr(addr)
            local_identifiers.add(canonical.lower())
            for alias in aliases:
                local_identifiers.add(alias.lower())
        except (socket.herror, socket.gaierror):
            continue  # Skip if resolution fails

    # Check if provided host (in lowercase) matches any identifier
    return host.lower() in local_identifiers

