    from collections import defaultdict
    deprecated_map = defaultdict(list)
    for option in self.spec_helper.iterate_option_specs():
        if len(option) < 2:
            continue
        name = option[0]
        spec = option[1]
        if isinstance(spec, dict) and 'deprecates' in spec:
            deprecates_val = spec['deprecates']
            if isinstance(deprecates_val, list):
                deprecated_map[name].extend(deprecates_val)
            else:
                deprecated_map[name].append(deprecates_val)
    return dict(deprecated_map)

