    :param answer_file_args: the dict of arguments from files
    """
    # Get the list of deprecated arguments
    deprecated_args = self.get_deprecated_args()

    found_deprecated = set()

    # Check CLI arguments for deprecated keys with non-None values
    for key, value in cli_args.items():
        if key in deprecated_args and value is not None:
            found_deprecated.add(key)

    # Check answer file arguments for deprecated keys with non-None values
    for key, value in answer_file_args.items():
        if key in deprecated_args and value is not None:
            found_deprecated.add(key)

    # Process found deprecated arguments
    if found_deprecated:
        # Log warning for each deprecated argument found
