def get_nested_custom_and_control_args(self, args):
    """
    Split input arguments to control, nested and custom.

    Controls arguments: control the IR behavior. These arguments
        will not be put into the spec yml file
    Nested arguments: are used by the Ansible playbooks and will be put
        into the spec yml file.
    Custom arguments: Custom ansible variables to be used instead of the
        normal nested usage.

    :param args: the collected list of args.
    :return: (dict, dict, dict): flat dicts (control_args, nested_args, custom_args)
    """
    control_args = {}
    nested_args = {}
    custom_args = {}

    for arg in args:
        if arg.startswith("--") or arg.startswith("-"):
            # Strip the dashes
            if arg.startswith("--"):
                arg = arg[2:]
            else:
                arg = arg[1:]

            # Split the argument into key and value if there's an '='
            if "=" in arg:
                key, value = arg.split("=", 1)
            else:
                key = arg
                value = True

            # Check for duplicate in control_args
            if key in control_args:
                self.LOG.warning("Duplicate control argument found: %s. Overwriting.", key)
            control_args[key] = value

        else:
            # For non-control arguments, split into key and value if there's an '='
            if "=" in arg:
                key, value = arg.split("=", 1)
            else:
                key = arg
                value = True

            # Check if it's a custom argument (starts with 'custom.')
            if key.startswith("custom."):
                if key in custom_args:
                    self.LOG.warning("Duplicate custom argument found: %s. Overwriting.", key)
                custom_args[key] = value
            else:
                if key in nested_args:
                    self.LOG.warning("Duplicate nested argument found: %s. Overwriting.", key)
                nested_args[key] = value

    return control_args, nested_args, custom_args

