import socket

def is_local(host):
    localhost = ['localhost', '127.0.0.1']
    local_ip = ['127.0.0.1', '::1']
    local_domain = ['localhost.localdomain', 'localhost']

    # Check if the host is in any of the predefined local lists
    if host in localhost or host in local_ip or host in local_domain:
        return True

    # Additional checks using socket library to handle IP and hostname resolution
    try:
        # Check if the host is an IP address and resolve it
        try:
            ip_address = socket.inet_aton(host)
            if ip_address in local_ip:
                return True
        except socket.error:
            # If inet_aton fails, it means the host is not a valid IPv4 address
            pass

        # Check if the host is a known hostname and resolve it to an IP
        resolved_host = socket.gethostbyname(host)
        if resolved_host in local_ip:
            return True

        # Check if the host is the current machine's hostname
        current_hostname = socket.gethostname()
        if host == current_hostname:
            return True

        # Check if the host resolves to the current machine's IP
        current_ip = socket.gethostbyname(current_hostname)
        if host == current_ip:
            return True

        # Additional check for IPv6
        try:
            ip_address = socket.inet_pton(socket.AF_INET6, host)
            if ip_address in local_ip:
                return True
        except socket.error:
            # If inet_pton fails, it means the host is not a valid IPv6 address
            pass
    except socket.error:
        pass

    return False

