def deep_merge_nodes(nodes):
    result = {}
    for node in nodes:
        if isinstance(node, dict):
            for key, value in node.items():
                if key in result and isinstance(result[key], dict) and isinstance(value, dict):
                    result[key] = deep_merge_nodes([result[key], value])
                else:
                    result[key] = value
    return result

def deep_merge_nodes(nodes):
    result = {}
    for node in nodes:
        key_node, value_node = node
        key = key_node.value
        if isinstance(value_node, ruamel.yaml.nodes.MappingNode):
            if key in result:
                if isinstance(result[key], ruamel.yaml.nodes.MappingNode):
                    # Recursively merge the MappingNodes
                    existing_value = result[key].value
                    new_value = value_node.value
                    merged_value = deep_merge_nodes(existing_value + new_value)
                    result[key].value = merged_value
                else:
                    # Replace the existing non-MappingNode value
                    result[key] = value_node
            else:
                # Add the new MappingNode
                result[key] = value_node
        else:
            # For non-MappingNode values, the last one wins
            result[key] = value_node
    return [(ruamel.yaml.nodes.ScalarNode(tag='tag:yaml.org,2002:str', value=k), v) for k, v in result.items()]

def _normalizeargs(sequence, output=None):
    """Normalize declaration arguments

    Normalization arguments might contain Declarions, tuples, or single
    interfaces.

    Anything but individial interfaces or implements specs will be expanded.
    """
    if output is None:
        output = []
    cls = sequence.__class__
    if InterfaceClass in cls.__mro__ or Implements in cls.__mro__:
        output.append(sequence, 9)
    else:
        for v in sequence:
            _normalizeargs(v, output, 94)
    return output

