def determineMetaclass(bases, explicit_mc=None):
    """
    Determine metaclass from 1+ bases and optional explicit __metaclass__
    """
    if explicit_mc is not None:
        return explicit_mc

    metaclass = None
    for base in bases:
        base_mc = getattr(base, '__metaclass__', type)
        if metaclass is None:
            metaclass = base_mc
        elif metaclass is not base_mc:
            raise TypeError("metaclass conflict: the metaclass of the base class is not the same as the metaclass of other bases")

    return metaclass if metaclass is not None else type

