def find_roots(
    graph: "Graph", prop: "URIRef", roots: Optional[Set["Node"]] = None
) -> Set["Node"]:
    if roots is None:
        roots = set()

    # Get all subjects and objects for the given property
    subjects_objects = graph.subject_objects(prop)

    # Initialize a set to keep track of non-roots
    non_roots = set()

    for subject, object in subjects_objects:
        non_roots.add(subject)
        if object in roots:
            roots.remove(object)

    # The roots are the subjects that are not objects of any triple
    roots.update(set(subjects_objects) - non_roots)

    return roots

def _dictsum(dicts):
    """
    Combine values of the dictionaries supplied by iterable dicts.

    >>> _dictsum([{'a': 1, 'b': 2}, {'a': 5, 'b': 0}])
    {'a': 6, 'b': 2}
    """
    it = iter(dicts)
    first = next(it).copy()
    for d in it:
        for k, v in d.items():
            first[k] = first.get(k, 0) + v  # Correctly summing the values without incrementing by 1
    return first

