def protocol_handlers(cls, protocol_version=None):
    if protocol_version is None:
        return None
    elif protocol_version == 1:
        return BoltProtocolHandlerV1()
    elif protocol_version == 2:
        return BoltProtocolHandlerV2()
    elif protocol_version == 3:
        return BoltProtocolHandlerV3()
    else:
        raise ValueError("Invalid protocol version")

def protocol_handlers(protocol_version=None):
    # Check if protocol_version is provided as a tuple
    if protocol_version is not None and not isinstance(protocol_version, tuple):
        raise TypeError("protocol_version must be passed in a tuple")

    # Define a dictionary to hold protocol handlers
    handlers = {
        (1, 0): BoltProtocolHandlerV1,
        (2, 0): BoltProtocolHandlerV2,
        (3, 0): BoltProtocolHandlerV3,
    }

    # If no protocol_version is provided, return all handlers
    if protocol_version is None:
        return handlers

