        )

    args = []
    accu = None
    for qs, qss, esc, pipe, word, white, fail in re.findall(RE_CMD_LEX, s):
        if word:
            accu = (accu or '') + word
        elif esc:
            accu = (accu or '') + esc[1]
        elif white or pipe:
