import re

def unquote(name):
    """Remove quote from the given name."""
    assert isinstance(name, str), "Name must be a string."

    def unquoted_char(match):
        """For each ;000 return the corresponding byte."""
        matched_str = match.group(0)
        if len(matched_str) != 4:
            return matched_str
        try:
            # Ensure the byte is correctly decoded using utf-8
            return bytes([int(matched_str[1:], 10)]).decode('utf-8')
        except ValueError:
            # Return the original string if decoding fails
            return matched_str

    # Correct the regex pattern to match the format ;000 where 000 are digits
    return re.sub(r';[0-9]{3}', unquoted_char, name)

