from typing import List, Optional

def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    if padding is not None:
        if padding < 1:
            raise ValueError("Padding must be a positive integer")
        number = number + (len(alphabet) ** padding)
    elif number < 0:
        raise ValueError("Number must be a non-negative integer")

    output = []
    while number:
        number, digit = divmod(number, len(alphabet))
        output.append(alphabet[digit])

    if padding is not None:
        output.extend([alphabet[0]] * (padding - len(output)))

    return ''.join(reversed(output))

from typing import List, Optional

def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    if number < 0:
        raise ValueError("Number must be a non-negative integer")

    if padding is not None:
        if padding < 1:
            raise ValueError("Padding must be a positive integer")

    output = []
    base = len(alphabet)

    # Convert number to the base specified by the length of the alphabet
    while number > 0:
        number, digit = divmod(number, base)
        output.append(alphabet[digit])

    # If padding is specified, ensure the output has the required length
    if padding is not None:
        while len(output) < padding:
            output.append(alphabet[0])

    return ''.join(reversed(output))

