def popitem(self):
    """Remove and return the `(key, value)` pair least frequently used."""
    if not self.__counter:
        raise KeyError('%s is empty' % type(self).__name__)

    key, _ = self.__counter.most_common(1)[0]
    value = self.pop(key)
    return (key, value)

