    found_tags = set()
    # <br> and <p> tags cause issues in us finding words - add some spacing around them
    new_text = text.replace("<br>", " <br> ").replace("<p>", " <p> ").replace("</p>", " </p> ")
    lines = new_text.splitlines(keepends=True)
    final_lines = []
    code_block = False
    final_text = None
