    # Additional check to ensure the config file contains the required 'host_key_checking' option
    config_path = os.environ['ANSIBLE_CONFIG']
    if not os.path.isfile(config_path):
        raise FileNotFoundError(f"The specified config file '{config_path}' does not exist.")

    with open(config_path, 'r') as config_file:
        config_content = config_file.read()

    if "host_key_checking = False" not in config_content:
        raise ValueError("The 'host_key_checking' option with value 'False' is not found in the 'defaults' section of the config file.")

    # Return the value of 'ANSIBLE_CONFIG' to ensure it is set
    return os.environ['ANSIBLE_CONFIG']

