def deep_merge_nodes(nodes):
    result = {}
    for node in nodes:
        if isinstance(node, dict):
            for key, value in node.items():
                if key in result and isinstance(result[key], dict) and isinstance(value, dict):
                    result[key] = deep_merge_nodes([result[key], value])
                else:
                    result[key] = value
    return result

def deep_merge_nodes(nodes):
    result = {}
    for node in nodes:
        key_node, value_node = node
        key = key_node.value

        if key in result:
            existing_value_node = result[key]
            if isinstance(existing_value_node, ruamel.yaml.nodes.MappingNode) and isinstance(value_node, ruamel.yaml.nodes.MappingNode):
                # Recursively merge the MappingNodes
                merged_value = deep_merge_nodes(zip(existing_value_node.value, value_node.value))
                result[key] = ruamel.yaml.nodes.MappingNode(tag='tag:yaml.org,2002:map', value=merged_value)
            else:
                # If not both MappingNodes, the last value wins
                result[key] = value_node
        else:
            result[key] = value_node

    # Convert the result dictionary back to a list of tuples
    return [(ruamel.yaml.nodes.ScalarNode(tag='tag:yaml.org,2002:str', value=k), v) for k, v in result.items()]

