def _normalizeargs(sequence, output=None):
    """Normalize declaration arguments

    Normalization arguments might contain Declarations, tuples, or single
    interfaces.

    Anything but individual interfaces or implements specs will be expanded.
    """
    if output is None:
        output = []

    # Check if the sequence is an instance of InterfaceClass
    if isinstance(sequence, InterfaceClass):
        output.append(sequence)
    else:
        # If the sequence is not an individual interface, iterate and normalize
        try:
            for v in sequence:
                _normalizeargs(v, output)
        except TypeError:
            # If sequence is not iterable, it might be a single implements spec
            output.append(sequence)

    return output

