def point_type(name, fields, srid_map):
    # Define a new class dictionary
    class_dict = {
        '__init__': lambda self, *args: Point.__init__(self, *args),
        'fields': fields,
        'srid_map': srid_map
    }

    # Create properties for each field
    for field in fields:
        class_dict[field] = property(lambda self, f=field: getattr(self, f))

    # Create the new class
    return type(name, (Point,), class_dict)

