def unit_of_work(metadata=None, timeout=None):
    def decorator(func):
        def wrapper(*args, **kwargs):
            # Assuming `f` is an API from the Oracle Context that manages transactions
            # Start the transaction with metadata and timeout
            if metadata is not None:
                f.set_metadata(metadata)
            if timeout is not None:
                f.set_timeout(timeout)

            try:
                # Execute the function within the transaction context
                result = func(*args, **kwargs)
                # Commit the transaction
                f.commit()
                return result
            except Exception as e:
                # Rollback the transaction in case of any error
                f.rollback()
                raise e

        return wrapper

    return decorator

