def _group_files_by_xml_filename(source: str, xmls: List[str], files: List[str]) -> Dict[str, List[str]]:
    # Extract the base name of the XML files
    xml_basenames = [splitext(basename(xml))[0] for xml in xmls]

    # Initialize the dictionary to store the results
    grouped_files = {}

    # Iterate over each file and group them by the XML basename
    for file in files:
        for xml_basename in xml_basenames:
