def determineMetaclass(bases, explicit_mc=None):
    """
    Determine metaclass from 1+ bases and optional explicit __metaclass__
    """
    if not bases:
        raise TypeError("At least one base class is required")

    # If an explicit metaclass is provided, use it
    if explicit_mc is not None:
        return explicit_mc

    # Find the metaclass from the bases
    metaclass = None
    for base in bases:
        base_mc = getattr(base, '__metaclass__', type)
        if metaclass is None:
            metaclass = base_mc
        elif metaclass is not base_mc:
            # If there are conflicting metaclasses, use the minimal bases metaclass
            if issubclass(metaclass, base_mc):
                metaclass = base_mc
            elif not issubclass(base_mc, metaclass):
                raise TypeError("Inconsistent metaclasses found: %s and %s" % (metaclass.__name__, base_mc.__name__))

    return metaclass

