class RecordExporter:
    def __init__(self, record):
        self.record = record

    def transform(self, key):
        if isinstance(key, int):
            if key < 0 or key >= len(self.record):
                raise IndexError("Index out of bounds")
            return list(self.record.items())[key]
        elif isinstance(key, str):
            return (key, self.record.get(key, None))
        else:
            raise TypeError("Key must be an integer or a string")

    def data(self, *keys):
        """
        Returns the keys and values of this record as a dictionary,
        optionally including only certain values by index or key. Keys
        provided in the items that are not in the record will be
        inserted with a value of None; indexes provided
        that are out of bounds will trigger an IndexError.

        Parameters:
        - keys: indexes or keys of the items to include; if none
                are provided, all values will be included
        Returns:
        - dictionary of values, keyed by field name
        Raises:
        - IndexError if an out-of-bounds index is specified
        """
        if not keys:
            return dict(self.record)

        result = {}
        for key in keys:
            transformed_key, value = self.transform(key)
            result[transformed_key] = value
        return result

