def _mergeOrderings(orderings):
    result = []
    seen = set()

    for ordering in orderings:
        # Find the first element in the current ordering that is already in the result
        first_in_result = None
        for element in ordering:
            if element in seen:
                first_in_result = element
                break

        if first_in_result is None:
            # If no element is in the result, append the whole ordering
            for element in ordering:
                if element not in seen:
                    result.append(element)
                    seen.add(element)
        else:
            # If some elements are in the result, find the position of the first element
            pos = result.index(first_in_result)
            # Insert the new elements before the first element found
