from datetime import datetime, timezone

class CustomTimezone:
    def __init__(self, tzinfo):
        self.tzinfo = tzinfo

    def utcoffset(self, dt):
        return self.tzinfo.utcoffset(dt)

    def dst(self, dt):
        return self.tzinfo.dst(dt)

    def enfold(self, dt):
        return dt.replace(fold=not dt.fold)

    def _fromutc(self, dt):
        if not dt.tzinfo:
            raise ValueError("The datetime object must be timezone-aware")

        # Convert dt to UTC
        dt_utc = dt.astimezone(timezone.utc)

        # Convert from UTC to the target timezone
        dt_target = dt_utc.astimezone(self.tzinfo)

        # Check for ambiguity and handle fold state
        if self.dst(dt_target) is not None:
            try:
                # Check if the datetime is ambiguous
                dt_fold = self.enfold(dt_target)
                if dt_fold != dt_target:
                    # If ambiguous, choose the first occurrence (fold=0)
                    dt_target = dt_target.replace(fold=0)
            except ValueError:
                pass

        return dt_target

