import re

def split(s, platform='this'):
    """
    Multi-platform variant of shlex.split() for command-line splitting.
    For use with subprocess, for argv injection etc. Using fast REGEX.

    Args:
    s (str): The string to split.
    platform (str|int): 'this' = auto from current platform;
                       1 = POSIX;
                       0 = Windows/CMD
                       (other values reserved)

    Returns:
    list: A list of split command-line arguments.

    Raises:
    ValueError: If an invalid platform value is provided.
    """
    if platform == 'this':
        import os
        if os.name == 'posix':
            platform = 1
        elif os.name == 'nt':
            platform = 0

    if platform == 1:
        # POSIX platform, split using '/'
        return re.findall(r'[^/]+', s)
    elif platform == 0:
        # Windows/CMD platform, split using '\\'
        return re.findall(r'[^\\]+', s)
    else:
        raise ValueError("Invalid platform value. Must be 'this', 1, or 0.")

# Example usage:
# print(split("path/to/file", platform='this'))
# print(split("path\\to\\file", platform=0))
# print(split("path/to/file", platform=1))

