class SpecParserException(Exception):
    pass

def _include_groups(self, parser_dict):
    """
    Resolves the include dict directive in the spec files.

    Args:
    parser_dict (dict): A dictionary containing the spec file directives.

    Returns:
    dict: A modified dictionary with resolved include directives.
    """
    if not isinstance(parser_dict, dict):
        raise SpecParserException("parser_dict must be a dictionary")

    resolved_dict = {}
    for key, value in parser_dict.items():
        if key == 'include':
            if isinstance(value, list):
                for item in value:
                    if isinstance(item, str):
                        # Assuming 'get' is a method to fetch included data
                        included_data = self.get(item)
                        if included_data is not None:
                            resolved_dict.update(included_data)
                        else:
                            raise SpecParserException(f"Include directive '{item}' not found")
                    else:
                        raise SpecParserException("Include directive must be a string")
            else:
                raise SpecParserException("Include value must be a list")
        else:
            resolved_dict[key] = value

    return resolved_dict

