    hour, minute, second, microsecond = value.hour, value.minute, value.second, value.microsecond
    nanoseconds = (hour * 3600 + minute * 60 + second) * 1000000000 + microsecond * 1000

    try:
        tz_offset = value.tzinfo.utcoffset(value).total_seconds() if value.tzinfo else 0
    except AttributeError:
        tz_offset = 0
    else:
        tz_offset = int(tz_offset * 1000)  # Convert to milliseconds

