def to_output(output: str, color: bool) -> None:
    if color and os.name == 'posix':
        # Simulate color output for POSIX systems
        print(f"\033[94m{output}\033[0m")
    else:
        print(output)

def cmd_fn(cmd: str, varargs: Sequence[str]) -> bool:
    # Simulate command execution
    process = subprocess.run(cmd + ' ' + ' '.join(varargs), shell=True)
    return process.returncode == 0

def _thread_mapper(target_concurrency: int, tasks: list[tuple[str, Sequence[str]]]) -> bool:
    results = []
    def worker(task):
        cmd, varargs = task
        result = cmd_fn(cmd, varargs)
        results.append(result)

    threads = []
    for i in range(0, len(tasks), target_concurrency):
        batch = tasks[i:i + target_concurrency]
        for task in batch:
            thread = threading.Thread(target=worker, args=(task,))
            threads.append(thread)
            thread.start()

        for thread in threads:
            thread.join()

    return all(results)

def xargs(
        cmd: tuple[str, ...],
        varargs: Sequence[str],
        *,
        color: bool = False,
        target_concurrency: int = 1,
        _max_length: int = _get_platform_max_length(),
        **kwargs
) -> None:
    normalized_cmd = normalize_cmd(cmd)
    partitions = []
    current_partition = []

    for arg in varargs:
        if len(' '.join(current_partition) + ' ' + arg) > _max_length:
            partitions.append((normalized_cmd, current_partition))
            current_partition = []
        current_partition.append(arg)

    if current_partition:
        partitions.append((normalized_cmd, current_partition))

    if _thread_mapper(target_concurrency, partitions):
        to_output("All tasks completed successfully.", color)
    else:
        raise Exception("Result not True!!!")

