class TTLCache:
    def __init__(self, maxsize=128, ttl=600, timer=time.monotonic, typed=False):
        self.cache = {}
        self.maxsize = maxsize
        self.ttl = ttl
        self.timer = timer
        self.typed = typed

    def __call__(self, func):
        @wraps(func)
        def wrapper(*args, **kwargs):
            key = args + tuple(sorted(kwargs.items())) if self.typed else args
            if key in self.cache:
