def difference(d1, d2, level=-1):
    if d2 is None:
        return d1.copy()

    if level == -1:
        return {k: v for k, v in d1.items() if k not in d2}
    elif level == 1:
        result = {}
        for k, v in d1.items():
            if k not in d2:
                result[k] = v
            elif isinstance(v, dict) and isinstance(d2[k], dict) and v != d2[k]:
