import os

class file_utils:
    @staticmethod
    def is_folder(folder):
        return os.path.isdir(folder)

    @staticmethod
    def files_list(folder):
        return [f for f in os.listdir(folder) if os.path.isfile(os.path.join(folder, f))]

    @staticmethod
    def xml_files_list(folder):
        return [f for f in file_utils.files_list(folder) if f.endswith('.xml')]

def _group_files_by_xml_filename(folder, all_files, xml_files):
    data = {}
    for xml_file in xml_files:
        base_name = os.path.splitext(xml_file)[0]
        data[base_name] = [f for f in all_files if f.startswith(base_name)]
    return data

def _explore_folder(folder):
    """
    Get packages' data from folder

    Groups files by their XML basename and returns data in dict format.

    Parameters
    ----------
    folder : str
        Folder of the package
    Returns
    -------
    dict
    """
    if file_utils.is_folder(folder):
        all_files = file_utils.files_list(folder)
        xml_files = file_utils.xml_files_list(folder)
        data = _group_files_by_xml_filename(folder, all_files, xml_files)
        return data
    else:
        raise ValueError(f"The provided path '{folder}' is not a valid folder.")

