class CustomTimezone:
    def __init__(self, offset_hours=0, offset_minutes=0, dst_hours=0, dst_minutes=0):
        self.offset = timedelta(hours=offset_hours, minutes=offset_minutes)
        self.dst_offset = timedelta(hours=dst_hours, minutes=dst_minutes)

    def utcoffset(self, dt):
        return self.offset

    def dst(self, dt):
        # Assuming DST is applied year-round for simplicity
        return self.dst_offset

    def enfold(self, dt, fold):
        # Create a new datetime with the specified fold state
        return dt.replace(fold=fold)

    def is_ambiguous(self, dt):
        # Check if the datetime is ambiguous
        try:
            dt1 = dt.replace(fold=0)
            dt2 = dt.replace(fold=1)
            return dt1.utcoffset() == dt2.utcoffset()
        except ValueError:
            return False

    def _fold_status(self, dt):
        # Determine the fold status of the datetime
        if self.is_ambiguous(dt):
            # If ambiguous, check if it's the first occurrence
            dt1 = dt.replace(fold=0)
            dt2 = dt.replace(fold=1)
            return 0 if dt1 < dt2 else 1
        return 0

    def _fromutc(self, dt):
        if not dt.tzinfo:
            raise ValueError("The datetime must be timezone-aware")

        # Convert the datetime to the new timezone
        dt_wall = dt.astimezone(self)

        # Determine if the datetime is ambiguous and in a "fold" state
        fold_status = self._fold_status(dt_wall)

        # Return the datetime with the correct fold status
        return self.enfold(dt_wall, fold_status)

