def size_to_bytes(size: str) -> int:
    units = {
        'B': 1,
        'K': 1024,
        'M': 1024 ** 2,
        'G': 1024 ** 3,
        'T': 1024 ** 4,
        'P': 1024 ** 5,
    }
    # Check if the last character is a digit, which means no unit is provided
    if size[-1].isdigit():
        return int(size)

    num = int(size[:-1])
    unit = size[-1].upper()

    # Ensure the unit is valid
    if unit in units:
        return num * units[unit]
    else:
        raise ValueError(f"Invalid unit: {unit}")

