    if d2 is None:
        return d1.copy()

    result = {}
    for key, value in d1.items():
        if key not in d2:
            result[key] = value
        elif level != 1:
            continue
        elif isinstance(value, dict) and isinstance(d2[key], dict):
            if level == -1 or level > 1:
                sub_diff = difference(value, d2[key], level - 1 if level > 1 else level)
                if sub_diff:
